$(document).ready(function(){
    $number = 1
    $("#1.col-lg-6").fadeIn('slow');
    $('.btn-next').click(function(){
        $number += 1
        if ($number == 2){
        $("#1.col-lg-6").hide();
        $('#2.col-lg-6').fadeIn('slow');
        }
        if ($number == 3){
        $("#2.col-lg-6").hide();
        $('#3.col-lg-6').fadeIn('slow');
        }
        if ($number == 4){
        $("#3.col-lg-6").hide();
        $('#4.col-lg-6').fadeIn('slow');
        }
        if ($number == 5){
            $number = 1
        $("#4.col-lg-6").hide();
        $('#1.col-lg-6').fadeIn('slow');
        }
    })
    $('.btn-back').click(function(){
        $number -= 1
        if ($number == 0){
            $number = 4
            $("#1.col-lg-6").hide();
            $('#4.col-lg-6').fadeIn('slow');
        }
        if ($number == 3){
            $("#4.col-lg-6").hide();
            $('#3.col-lg-6').fadeIn('slow');
        }
        if ($number == 2){
            $("#3.col-lg-6").hide();
            $('#2.col-lg-6').fadeIn('slow');
        }
        if ($number == 1){
            $("#2.col-lg-6").hide();
            $('#1.col-lg-6').fadeIn('slow');
        }
    })
})